-- ************************************** INVENTORY_DIMENSION
CREATE TABLE INVENTORY_DIMENSION
(
 INVENTORY_ID     number NOT NULL COMMENT 'Unique identifier for each inventory record.',
 MATERIAL_ID      char(18) COMMENT 'References the material stored in the inventory.',
 PLANT_ID         char(4) COMMENT 'References the plant where the inventory is located.',
 QUANTITY_ON_HAND decimal(15,3) COMMENT 'The quantity of the material currently available at the plant.',
 LAST_UPDATED     date COMMENT 'The last date when the inventory record was updated.',

 CONSTRAINT PK_INVENTORY_DIMENSION PRIMARY KEY ( INVENTORY_ID ),
 CONSTRAINT FK_MATERIAL_ID FOREIGN KEY ( MATERIAL_ID ) REFERENCES SC.MATERIAL ( MATERIAL_ID ),
 CONSTRAINT FK_PLANT_ID FOREIGN KEY ( PLANT_ID ) REFERENCES SC.PLANT ( PLANT_ID )
)
COMMENT = 'Dimension table for inventory details, capturing material availability and updates by plant.';
