-- ************************************** POS.FEEDBACK
CREATE TABLE POS.FEEDBACK
(
 FEEDBACK_ID    number NOT NULL COMMENT 'A unique identifier for each feedback entry.',
 CUSTOMER_ID    number COMMENT 'The customer who provided the feedback, linked to the Customer table.',
 ORDER_ID       number COMMENT 'The order that the feedback relates to, linked to the Order table.',
 RATING         number COMMENT 'A numeric rating provided by the customer.',
 COMMENTS       varchar2(500) COMMENT 'The feedback comments from the customer.',
 SUBMITTED_DATE date COMMENT 'The date when the feedback was submitted.',

 CONSTRAINT PK_FEEDBACK PRIMARY KEY ( FEEDBACK_ID ),
 CONSTRAINT CUSTOMER_ID FOREIGN KEY ( CUSTOMER_ID ) REFERENCES POS.CUSTOMER ( CUSTOMER_ID ),
 CONSTRAINT ORDER_ID FOREIGN KEY ( ORDER_ID ) REFERENCES POS."ORDER" ( ORDER_ID )
)
COMMENT = 'Table stores customer feedback related to orders.';
