-- ************************************** POS.Inventory
CREATE TABLE POS.Inventory
(
InventoryId     numeric NOT NULL PRIMARY KEY NOT ENFORCED OPTIONS(description = 'A unique identifier for each inventory record.'),
LocationId      numeric REFERENCES POS.Location(LocationId) NOT ENFORCED OPTIONS(description = 'The location of the inventory, linked to the Location table.'),
ProductName     string OPTIONS(description = 'The name of the product.'),
QuantityOnHand  numeric OPTIONS(description = 'The number of items available in stock.'),
Unit            string OPTIONS(description = 'The unit of measurement.'),
LastUpdatedDate date OPTIONS(description = 'The date when the inventory was last updated.')
)
OPTIONS(description = 'Table tracks the stock of products at each location, including quantities and units of measure.',
friendly_name = 'Inventory');
