CREATE TABLE POS."ORDER"
(
 ORDER_ID     number NOT NULL COMMENT 'A unique identifier for each order.',
 LOCATION_ID  number COMMENT 'The location where the order was placed, linked to the Location table.',
 ORDER_DATE   date COMMENT 'The date when the order was placed.',
 TOTAL_AMOUNT decimal(15, 2) COMMENT 'The total value of the order.',
 STATUS       varchar2(20) COMMENT 'The status of the order (e.g., "Completed", "Pending").',

 CONSTRAINT PK_ORDER PRIMARY KEY ( ORDER_ID ),
 CONSTRAINT LOCATION_ID FOREIGN KEY ( LOCATION_ID ) REFERENCES POS.LOCATION ( LOCATION_ID )
)
COMMENT = 'Table stores the details for customer orders placed at the business.';
