-- ************************************** SALES_FACT
CREATE TABLE SALES_FACT
(
 SALE_ID       number NOT NULL COMMENT 'Unique identifier for each sales transaction.',
 DATE_ID       date NOT NULL COMMENT 'Unique identifier for the date.',
 MATERIAL_ID   char(18) COMMENT 'References the material sold in the transaction.',
 PLANT_ID      char(4) COMMENT 'References the plant associated with the transaction.',
 INVENTORY_ID  number COMMENT 'References the inventory record for the material at the plant.',
 QUANTITY_SOLD decimal(15,3) COMMENT 'Quantity of the material sold.',
 REVENUE       decimal(15,2) COMMENT 'Total revenue generated by the transaction.',
 SALE_DATE_ID  date COMMENT 'Date of the sale, references the date dimension.',

 CONSTRAINT PK_SALES_FACT PRIMARY KEY ( SALE_ID ),
 CONSTRAINT FK_4 FOREIGN KEY ( DATE_ID ) REFERENCES DATE_DIMENSION ( DATE_ID ),
 CONSTRAINT FK_INVENTORY_ID FOREIGN KEY ( INVENTORY_ID ) REFERENCES SC.INVENTORY ( INVENTORY_ID ),
 CONSTRAINT FK_MATERIAL_ID FOREIGN KEY ( MATERIAL_ID ) REFERENCES SC.MATERIAL ( MATERIAL_ID ),
 CONSTRAINT FK_PLANT_ID FOREIGN KEY ( PLANT_ID ) REFERENCES SC.PLANT ( PLANT_ID )
)
COMMENT = 'Fact table for storing sales data, capturing quantity sold, revenue, and associated material, plant, and inventory details.';
