-- ************************************** SC.PURCHASE_ORDER
CREATE TABLE SC.PURCHASE_ORDER
(
 PURCHASE_ORDER_ID char(10) NOT NULL COMMENT 'Unique identifier for each purchase order.',
 SUPPLIER_ID       char(10) COMMENT 'References the supplier from whom the order was placed.',
 ORDER_DATE        date COMMENT 'The date when the purchase order was created.',
 STATUS            varchar(20) COMMENT 'The current status of the purchase order (e.g., Pending, Received).',
 TOTAL_AMOUNT      decimal(15,2) COMMENT 'The total monetary value of the purchase order.',

 CONSTRAINT PK_PURCHASE_ORDER PRIMARY KEY ( PURCHASE_ORDER_ID ),
 CONSTRAINT SUPPLIER_ID FOREIGN KEY ( SUPPLIER_ID ) REFERENCES SC.SUPPLIER ( SUPPLIER_ID )
)
COMMENT = 'Stores purchase orders placed with suppliers, including order status and total value.';
